import React, { useEffect, useState } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import DiseaseService from '../services/DiseaseService'
import BasicSnackBar from './BasicSnackBar';
import Button from '@mui/material/Button';

const ListDisease = () => {
    const history = useHistory();
    const idDoctor = localStorage.getItem('token');
    const location = useLocation();

    // Aqui se empiezan a definir las variables de estado
    const [diseases, setDiseases] = useState([]);
    const [open, setOpen] = useState(false);
    



    // Aqui se empiezan a definir las funciones del componente
    const deleteDisease = (idDisease) => {
        DiseaseService.deleteDisease(idDisease).then(() => {
            const filteredDiseases = diseases.filter(disease => disease.id !== idDisease);
            setDiseases(filteredDiseases);
        });
        DiseaseService.deleteSymptomsOfDisease(idDisease);
        window.location.reload();
        history.push('/list-diseases/');
    }

    const viewDisease = (idDisease) => {
        history.push(`/view-disease/${idDisease}`);
    }

    const editDisease = (idDisease) => {
        history.push(`/update-disease/${idDisease}`);
    }

    const addDisease = () => {
        history.push('/add-disease/add');
        handleClick();
    }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
        return;
        }

    setOpen(false);
    };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push(`/admin-view/${idDoctor}`);
        }
        console.log(localStorage.getItem('token'));
        DiseaseService.getDiseases().then((res) => {
            setDiseases(res.data);
        });
        if(location.search === 'true'){
            console.log(location.search);
            setOpen(location.search);
        }
    }, [location]);

    return (
        <div>
            <h2 className="text-center">Lista de Enfermedades</h2>
            <div className="row">
                <button className="btn btn-primary" onClick={() => addDisease()}> AÃ±adir Enfermedad</button>
            </div>
            <div className="row">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th>Nombre</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            diseases.map(
                                disease =>
                                    <tr key={disease.idDisease}>
                                        <td>{disease.diseaseName}</td>
                                        <td>
                                            <button style={{ marginLeft: "10px" }} onClick={() => editDisease(disease.idDisease)} className="btn btn-info">Update</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => deleteDisease(disease.idDisease)} className="btn btn-danger">Borrar</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => viewDisease(disease.idDisease)} className="btn btn-info">Detalles</button>
                                            {/* <BasicSnackBar 
                                                open={open}
                                                onClose={handleClose}
                                                severity="success"
                                                message="Enfermedad aÃ±adida correctamente"
                                            /> */}
                                        </td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>
                <BasicSnackBar 
                    open={open}
                    onClose={handleClose}
                    severity="success"
                    message="Enfermedad aÃ±adida correctamente"
                />
            </div>
        </div>
    )

}

export default ListDisease;
