import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import UserService from '../../services/UserService'

const ViewUser = () => {
    const params = useParams();
    const history = useHistory();

    // Aqui se empiezan a definir las variables de estado
    const [id, setId] = useState(params.id);
    const [user, setUser] = useState({})

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push(`/list-patients/${id}`);
        }
        UserService.getUserById(id).then((res) => {
            setUser(res.data);
        })
    }, []);

    const updateUser = () => {
        history.push(`/update-user/${id}`);
    }

    const deleteUser = () => {
        UserService.deleteUser(id).then(() => {
        });
        localStorage.removeItem('token'); //Cuando se borre cerramos sesión
        setId('')
        history.push('/');
    }
    return (
        <div>
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Detalles del usuario</h3>
                <div className="card-body">
                    <div className="row">
                        <label> Nombre: </label>
                        <div> {user.firstName}</div>
                    </div>
                    <div className="row">
                        <label> Apellidos: </label>
                        <div> {user.lastName}</div>
                    </div>
                    <div className="row">
                        <label> Email: </label>
                        <div> {user.email}</div>
                    </div>
                </div>
                <div className="row">
                <button className="btn btn-primary" onClick={() => updateUser()}>Modificar datos</button>
                <button className="btn btn-danger" onClick={() => deleteUser()}>Eliminar usuario</button>
                </div>
            </div>
            <br></br>
        </div>
    )
}

export default ViewUser;
