import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Header = () => {
    const history = useHistory();


    // Aqui se empiezan a definir las variables de estado
    const [idUser, setIdUser] = useState(localStorage.getItem('token')); //Cambiar a ponerlo a un useEffect() al principio


    // Aqui se empiezan a definir las funciones del componente
    const viewUser = (id) => {
        history.push(`/view-user/${id}`);
    }

    const renderUser = () => {
        if (idUser) {
            return <button style={{ marginLeft: "10px" }} onClick={() => viewUser(idUser)} className="btn btn-info">{idUser}</button>
        }
    }

    const logoutUser = () => {
        localStorage.removeItem('token');
        setIdUser('')
        history.push('/');
    }

    const renderLogout = () => {
        if (idUser) {
            return <button className="btn btn-success" onClick={() => logoutUser()}>Logout</button>
        }
    }

    return (
        <div>
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="https://www.google.es" className="navbar-brand">Aplicaci√≥n Web</a></div>
                    <div>
                        {
                            renderUser()
                        }
                        {
                            renderLogout()
                        }
                    </div>
                </nav>
            </header>
        </div>
    )

}

export default Header;
