import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import PatientService from '../services/PatientService'

const ListPatient = () => {
    const history = useHistory();
    const idDoctor = localStorage.getItem('token');

    // Aqui se empiezan a definir las variables de estado
    const [patients, setPatients] = useState([]);


    // Aqui se empiezan a definir las funciones del componente

    const viewPatient = (idPatient) => {
        history.push(`/view-patient/${idDoctor}/${idPatient}`);
    }

    // const addPatient = () => {
    //     history.push('/add-patient/add');
    // }

    useEffect(() => {
        window.onpopstate = () => {
            localStorage.removeItem('token');
            setIdUser('')
            history.push('/');
        }
        console.log(localStorage.getItem('token'));
            PatientService.getPatientsOfDoctor(idDoctor).then((res) => {
            setPatients(res.data);
        });
    }, []);

    return (
        <div>
            <h2 className="text-center">Lista de Pacientes</h2>
            <div className="row">
                {/* <button className="btn btn-primary" onClick={() => addPatient()}> AÃ±adir paciente</button> */}
            </div>
            <div className="row">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th>DNI</th>
                            <th>Nombre</th>
                            <th>Apellidos</th>
                            <th>Email</th>
                            <th>Fecha Nacimiento</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            patients.map(
                                patient =>
                                    <tr key={patient.idDoctor}>
                                        <td>{patient.idPatient}</td>
                                        <td>{patient.firstName}</td>
                                        <td>{patient.lastName}</td>
                                        <td>{patient.email}</td>
                                        <td>{patient.birthday}</td>   
                                        <td>
                                            {/* <button onClick={() => editPatient(patient.idPatient)} className="btn btn-info">Update</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => deletePatient(patient.id)} className="btn btn-danger">Borrar</button> */}
                                            <button style={{ marginLeft: "10px" }} onClick={() => viewPatient(patient.idPatient)} className="btn btn-info">Ver perfil</button>
                                        </td> 
                                    </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>


        </div>
    )
}

export default ListPatient;
