import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import RecordService from '../services/RecordService';

const CreateRecord = () => {
    const params = useParams();
    const history = useHistory();
    const idDoctor = localStorage.getItem('token');


    // Aqui se empiezan a definir las variables de estado
    const [id] = useState(params.id);
    const [idP] = useState(params.idPatient);
    // const [idPatient, setIdPatient] = useState('');
    // const [firstName, setFirstName] = useState('');
    // const [lastName, setLastName] = useState('');
    // const [email, setEmail] = useState('');
    const [dateRecord, setDateRecord] = useState('');


    // Aqui se empiezan a definir las funciones del componente
    const saveOrUpdateRecord = () => {
        let record = { date: date, idPatient: idP };
        console.log('record => ' + JSON.stringify(record));

        if (id === 'add') {
            RecordService.createRecord(record).then(() => {
                //history.push(`/list-records/${idDoctor}`);
                history.push(`/list-records/${idDoctor}/${idP}`);
            });
        }
    }

    const changeDateHandler = (event) => {
        setDateRecord(event.target.value);
    }

    const cancel = () => {
        history.push(`/list-records/${idDoctor}/${idP}`);
    }

    const getTitle = () => {
        if (id === 'add') {
            return <h3 className="text-center">AÃ±adir Registro</h3>
        } else {
            return <h3 className="text-center">Modificar Registro</h3>
        }
    }

    useEffect(() => {
        window.onpopstate = () => {
            history.push(`/list-records/${idDoctor}/${idP}`);
        }
        }, []);

    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        {
                            getTitle()
                        }
                        <div className="card-body">
                            <form onSubmit={() => saveOrUpdateRecord()}>
                                <div className="form-group">
                                    <label> Fecha: </label>
                                    <input type="date" placeholder="Fecha" name="dateRecord" className="form-control" value={dateRecord} onChange={(event) => changeDateHandler(event)} />
                                </div>
                                <button type="submit" className="btn btn-success">Guardar</button>
                                <button className="btn btn-danger" onClick={() => cancel()} style={{ marginLeft: "10px" }}>Cancelar</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default CreateRecord;
