import { Avatar } from '@mui/material';
import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import UserService from '../../services/UserService'

const Header = () => {
    const history = useHistory();

    // Aqui se empiezan a definir las variables de estado
    const [idUser, setIdUser] = useState(localStorage.getItem('token')); //Cambiar a ponerlo a un useEffect() al principio
    const [user, setUser] = useState({})

    // Aqui se empiezan a definir las funciones del componente
    const viewUser = (id) => {
        history.push(`/view-user/${id}`);
    }

    const renderTitle = () => {
        if (idUser) {
            //Algo
            return <button style={{ marginLeft: "10px" }} onClick={() => goToTitle()} className="btn btn-info">TFG</button>
        }
    }

    const goToTitle = () => {
        if (idUser) {
            if (idUser == 0) {
                history.push(`/admin-view/${idUser}`);
            }
            else {
                history.push(`/list-patients/${idUser}`);
            }
        }
    }

    const renderUser = () => {
        if (idUser) {
            //return <button style={{ marginLeft: "5px" }} onClick={() => viewUser(idUser)} className="btn btn-info">{user.firstName}</button>
            //return <img src= {user.userPicture} alt="new" onClick={() => viewUser(idUser)}/>
            return <Avatar alt="User Picture" style={{ marginRight: "300px" }} src={user.userPicture} onClick={() => viewUser(idUser)}/>
        }
    }

    const logoutUser = () => {
        localStorage.removeItem('token');
        setIdUser('')
        history.push('/');
    }

    const renderLogout = () => {
        if (idUser) {
            return <button className="btn btn-success" onClick={() => logoutUser()}>Logout</button>
        }
    }

    useEffect(() => {
        window.onpopstate = () => {
            history.push('/');
        }
        //Comprobar que la infomacion de login es correcta
        UserService.getUserById(idUser).then((res) => {
            setUser(res.data);
        });
    }, []);

    return (
        <div>
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div>
                        {
                            renderTitle()
                        }
                    </div>
                    <div className="info-user">
                        {renderUser()}
                        {renderLogout()}
                    </div>
                </nav>
            </header>
        </div>
    )

}

export default Header;
