import React, { useEffect, useState } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import MedicineService from '../../services/MedicineService'
import BasicAlertDialog from '../common/BasicAlertDialog';
import BasicSnackBar from '../common/BasicSnackBar';

const ListMedicine = () => {
    const history = useHistory();
    const idDoctor = localStorage.getItem('token');
    const location = useLocation();


    // Aqui se empiezan a definir las variables de estado
    const [medicines, setMedicines] = useState([]);
    const [open, setOpen] = useState(false);
    const [dialogOpen, setDialogOpen] = React.useState(false);


    // Aqui se empiezan a definir las funciones del componente
    const deleteMedicine = (idMedicine) => {
        MedicineService.deleteMedicine(idMedicine).then(() => {
            const filteredMedicines = medicines.filter(medicine => medicine.id !== idMedicine);
            setMedicines(filteredMedicines);
        });
        history.push('/list-medicines/');
    }

    const viewMedicine = (idMedicine) => {
        history.push(`/view-medicine/${idMedicine}`);
    }

    const editMedicine = (idMedicine) => {
        history.push(`/update-medicine/${idMedicine}`);
    }

    const addMedicine = () => {
        history.push('/add-medicine/add');
    }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpen(false);
    };

    ///////////////////

    const handleCloseDialog = () => {
        setDialogOpen(false);
    };

    // const handleCloseDialog = (event, reason) => {
    //     if (reason === 'clickaway') {
    //         return;
    //     }
    //     (false);
    // };

    const handleClickOpen = () => {
        setDialogOpen(true);
    };

    const handleDelete = (idM) => {
        // const confirmed = window.confirm('¿Estás seguro de que deseas eliminar este elemento?');
        // if (confirmed) {
        //     deleteMedicine(idM);
        // }
        deleteMedicine(idM);
      };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push(`/admin-view/${idDoctor}`);
        }
        MedicineService.getMedicines().then((res) => {
            setMedicines(res.data);
        });
        if (location.search === '?true') {
            handleClick();
        }
    }, [location]);

    return (
        <div>
            <h2 className="text-center">Lista de Medicamentos</h2>
            <div className="row">
                <button className="btn btn-primary" onClick={() => addMedicine()}> Añadir Medicamento</button>
            </div>
            <div className="row">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th>Nombre</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            medicines.map(
                                medicine =>
                                    <tr key={medicine.idMedicine}>
                                        <td>{medicine.medicineName}</td>
                                        <td>
                                            <button style={{ marginLeft: "10px" }} onClick={() => editMedicine(medicine.idMedicine)} className="btn btn-info">Update</button>
                                            {/* <button style={{ marginLeft: "10px" }} onClick={() => deleteMedicine(medicine.idMedicine)} className="btn btn-danger">Borrar</button> */}
                                            <button style={{ marginLeft: "10px" }} onClick={() => handleDelete(medicine.idMedicine)} className="btn btn-danger">Borrar</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => viewMedicine(medicine.idMedicine)} className="btn btn-info">Detalles</button>
                                        </td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>
                <BasicSnackBar
                    open={open}
                    onClose={handleClose}
                    severity="success"
                    message="Medicina añadida correctamente"
                />
                <BasicAlertDialog
                open={dialogOpen}
                onClose={handleCloseDialog}
                onClickCancel={handleCloseDialog}
                onClickDelete={deleteMedicine}
                ></BasicAlertDialog>
            </div>
        </div>
    )

}

export default ListMedicine;
