import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import SymptomService from '../services/SymptomService'

const ListSymptom = () => {
    const history = useHistory();


    // Aqui se empiezan a definir las variables de estado
    const [symptoms, setSymptoms] = useState([]);

    const deleteSymptom = (idSymptom) => {
        SymptomService.deleteSymptom(idSymptom).then(() => {
            const filteredSymptoms = symptoms.filter(symptom => symptom.id !== idSymptom);
            setSymptoms(filteredSymptoms);
        });
        history.push('/list-symptoms/');
    }

    const viewSymptom = (idSymptom) => {
        history.push(`/list-symptoms/${idSymptom}`);
    }

    const addSymptom = () => {
        history.push('/add-symptom/add');
    }

    useEffect(() => {
        window.onpopstate = () => {
            localStorage.removeItem('token');
            history.push('/');
        }
        console.log(localStorage.getItem('token'));
        SymptomService.getSymptoms().then((res) => {
            setSymptoms(res.data);
        });
    }, []);

    return (
        <div>
            <h2 className="text-center">Lista de Síntomas</h2>
            <div className="row">
                <button className="btn btn-primary" onClick={() => addSymptom()}> Añadir Síntomas</button>
            </div>
            <div className="row">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th>Nombre</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            symptoms.map(
                                symptom =>
                                    <tr key={symptom.idSymptom}>
                                        <td>{symptom.name}</td>
                                        <td>
                                            <button style={{ marginLeft: "10px" }} onClick={() => deleteSymptom(symptom.idSymptom)} className="btn btn-danger">Borrar</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => viewSymptom(symptom.idSymptom)} className="btn btn-info">Detalles</button>
                                        </td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )


}

export default ListSymptom;
