import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Header = () => {
    const history = useHistory();


    // Aqui se empiezan a definir las variables de estado
    const [idUser, setIdUser] = useState(localStorage.getItem('token')); //Cambiar a ponerlo a un useEffect() al principio


    // Aqui se empiezan a definir las funciones del componente
    const viewUser = (id) => {
        history.push(`/view-user/${id}`);
    }

    const renderTitle = () => {
        if (idUser) {
            //history.push(`/list-patients/${idUser}`);
            return <button style={{ marginLeft: "10px" }} onClick={() => goToTitle()} className="btn btn-info">TFG</button>
        }
        // else{
        //     return <a className="navbar-brand">Aplicación Web</a> 
        // }
    }

    const goToTitle = () => {
        if (idUser) {
            if(idUser == 0){
                history.push(`/admin-view/${idUser}`);
            }
            else{
                history.push(`/list-patients/${idUser}`);
            }   
            //return <button style={{ marginLeft: "10px" }} onClick={() => viewUser(idUser)} className="btn btn-info">TFG</button>
        }
    }

    const renderUser = () => {
        if (idUser) {
            return <button style={{ marginLeft: "10px" }} onClick={() => viewUser(idUser)} className="btn btn-info">{idUser}</button>
        }
    }

    const logoutUser = () => {
        localStorage.removeItem('token');
        setIdUser('')
        history.push('/');
    }

    const renderLogout = () => {
        if (idUser) {
            return <button style={{ marginLeft: "20px" }} className="btn btn-success" onClick={() => logoutUser()}>Logout</button>
        }
    }

    return (
        <div>
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    {/* <div><a href="https://www.google.es" className="navbar-brand">Aplicación Web</a></div> */}
                    <div>
                         {
                            renderTitle()
                         }
                    </div>
                    <div className="info-user">
                        {
                            renderUser()
                        }
                        {
                            renderLogout()
                        }
                    </div>
                </nav>
            </header>
        </div>
    )

}

export default Header;
