import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom';


const AdminView = () => {
    const history = useHistory();

    const handleListSymptom = () => listSymptom()

    const listSymptom = () => {
        history.push('/list-symptoms');
    }

    const listDisease = () => {
        history.push('/list-diseases');
    }

    const listMedicine = () => {
        history.push('/list-medicines');
    }

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            localStorage.removeItem('token');
            history.push('/');
        }
    }, []);

    return (
        <div>
            <h2 className="text-center">Bienvenido Administrador</h2>
            <div className="row">
                <button className="btn btn-primary" onClick={handleListSymptom}> Lista Sintomas</button>
                <button className="btn btn-primary" onClick={() => listDisease()}> Lista Enfermedades</button>
                <button className="btn btn-primary" onClick={() => listMedicine()}> Lista Medicamentos</button>
            </div>
        </div>
    )
}

export default React.memo(AdminView);
