import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import UserService from '../services/UserService';

const UpdateUser = () => {
    const params = useParams();
    const history = useHistory();

    // Aqui se empiezan a definir las variables de estado
    const [idUser, setIdUser] = useState(localStorage.getItem('token')); //Cambiar a ponerlo a un useEffect() al principio
    const [id] = useState(params.id);
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');


    // Aqui se empiezan a definir las funciones del componente
    const UpdateUser = () => {
        let user = { firstName: firstName, lastName: lastName, email: email, password: password };
        console.log('user => ' + JSON.stringify(user));
        
            UserService.updateUser(user, id).then(() => {
                //Aqui tengo que cerrar la sesion
                localStorage.removeItem('token');
                setIdUser('')
                history.push('/');
            });
    }

    const changeFirstNameHandler = (event) => {
        setFirstName(event.target.value);
    }

    const changeLastNameHandler = (event) => {
        setLastName(event.target.value);
    }

    const changeEmailHandler = (event) => {
        setEmail(event.target.value);
    }

    const changePasswordHandler = (event) => {
        setPassword(event.target.value);
    }

    const cancel = () => {
        history.push('/');
    }

    const getTitle = () => {
        return <h3 className="text-center">Modificar Usuario</h3>
    }


    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push('/');
        }
        
        // if (id === 'add') {
        //     return
        // } 
        // else {
        //     UserService.getUserById(id).then((res) => {
        //         const user = res.data;

        //         setFirstName(user.firstName);
        //         setLastName(user.lastName);
        //         setEmail(user.emailId);
        //     });
        // }
    });

    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        {
                            getTitle()
                        }
                        <div className="card-body">
                            <form onSubmit={() => UpdateUser()}>
                                <div className="form-group">
                                    <label>Nombre: </label>
                                    <input placeholder="Nombre" name="firstName" className="form-control" value={firstName} onChange={(event) => changeFirstNameHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Apellidos: </label>
                                    <input placeholder="Apellidos" name="lastName" className="form-control" value={lastName} onChange={(event) => changeLastNameHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Email: </label>
                                    <input placeholder="Email" name="email" className="form-control" value={email} onChange={(event) => changeEmailHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Contraseña: </label>
                                    <input placeholder="Contraseña" name="password" className="form-control" value={password} onChange={(event) => changePasswordHandler(event)} />
                                </div>
                                <button type="submit" className="btn btn-success">Guardar</button>
                                <button type="button" className="btn btn-danger" onClick={() => cancel()} style={{ marginLeft: "10px" }}>Cancelar</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default UpdateUser;
