import React, { useEffect, useState } from 'react';
import { useHistory, useLocation, useParams } from 'react-router-dom';
import AppointmentService from '../services/AppointmentService'
import SearchPatient from './SearchPatient';
import BasicSnackBar from './BasicSnackBar';

const ListAppointment = () => {
    const history = useHistory();
    const params = useParams();
    const idDoctor = localStorage.getItem('token');
    const location = useLocation();

    // Aqui se empiezan a definir las variables de estado
    const [appointments, setAppointments] = useState([]);
    const [open, setOpen] = useState(false);
    const [order, setOrder] = React.useState(useState(params.order));


    // Aqui se empiezan a definir las funciones del componente
    const deleteAppointment = (idAppointment) => {
        AppointmentService.deleteAppointment(idAppointment).then(() => {
            const filteredAppointments = appointments.filter(appointment => appointment.id !== idAppointment);
            setAppointments(filteredAppointments);
        });
        history.push(`/list-appointments/${idDoctor}`);
    }

    const viewAppointment = (idAppointment) => {
        history.push(`/view-appointment/${idDoctor}/${idAppointment}`);
    }

    const editAppointment = (idAppointment) => {
        history.push(`/update-appointment/${idAppointment}`);
    }

    const addAppointment = () => {
        history.push('/add-appointment/add');
    }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            //localStorage.removeItem('token');
            history.push(`/list-patients/${idDoctor}`);
        }
        console.log(localStorage.getItem('token'));
        AppointmentService.getAppointmentsOfDoctor(idDoctor).then((res) => {
            setAppointments(res.data);
        });
        console.log(location.search);
        if(location.search === '?true'){
            handleClick();
        }
    }, [location]);

    return (
        <div>
            <h2 className="text-center">Lista de Citas</h2>
            <div className="row">
                <button className="btn btn-primary" style={{ marginRight: "703px" }} onClick={() => addAppointment()}> Añadir cita</button>
                {/* <button className="btn btn-primary" onClick={() => listSymptom()}> Lista Sintomas</button>
                <button className="btn btn-primary" onClick={() => listDisease()}> Lista Enfermedades</button>
                <button className="btn btn-primary" onClick={() => listMedicine()}> Lista Medicamentos</button> */}
                {/* <SearchPatient patients={patients}/> */}
            </div>
            {/* <div className="row">
                <SearchPatient patients={patients}/>
            </div> */}
            <div className="row">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th>Paciente</th>
                            <th>Fecha</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            appointments.map(
                                appointment =>
                                    <tr key={appointment.idAppointment}>
                                        <td>{appointment.idPatient}</td>
                                        <td>{appointment.dateAppointment}</td>
                                        <td>
                                            {/* <button style={{ marginLeft: "10px" }} onClick={() => editAppointment(appointment.idAppointment)} className="btn btn-info">Update</button> */}
                                            <button style={{ marginLeft: "10px" }} onClick={() => deleteAppointment(appointment.idAppointment)} className="btn btn-danger">Borrar</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => viewAppointment(appointment.idAppointment)} className="btn btn-info">Ver cita</button>
                                        </td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>
                <BasicSnackBar 
                    open={open}
                    onClose={handleClose}
                    severity="success"
                    message="Cita añadida correctamente"
                />
            </div>
        </div>
    )
}

export default ListAppointment;
