import React, { useEffect, useState } from 'react';
import { useHistory, useParams, useLocation } from 'react-router-dom';
import DiseaseService from '../services/DiseaseService'
import BasicSnackBar from './BasicSnackBar'

const ViewDisease = () => {
    const history = useHistory();
    const params = useParams();
    const location = useLocation();

    // Aqui se empiezan a definir las variables de estado
    const [idD] = useState(params.id);
    const [disease, setDisease] = useState({})
    const [symptoms, setSymptoms] = useState([])

    const addSymptomToDisease = () => {
        history.push(`/add-symptom-to-disease/${idD}`);
    }

    const deleteSymptom = (idDS) => {
        DiseaseService.deleteSymptomOfDisease(idD, idDS).then(() => {
            const filteredSymptoms = symptoms.filter(symptom => symptom.id !== idDS);
            setSymptoms(filteredSymptoms);
        });
        window.location.reload();
    }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push('/list-diseases/');
        }
        DiseaseService.getDiseaseById(idD).then((res) => {
            setDisease(res.data);
        });
        //Busco los sintomas de la enfermedad con idD
        DiseaseService.getSymptomsOfDisease(idD).then((res) => {
            setSymptoms(res.data);
        });
        console.log(location.search);
        if(location.search === '?true'){
            handleClick();
        }
    }, [location]);

    return (
        <div>
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Detalles de la enfermedad</h3>
                <div className="card-body">
                    <div className="row">
                        <label> Nombre: </label>
                        <div> {disease.diseaseName}</div>
                    </div>
                    <div className="row">
                        <label> Descripción: </label>
                        <div> {disease.diseaseDescription}</div>
                    </div>
                    <div className="row">
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th>Sintomas</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    symptoms.map(
                                        symptom =>
                                            <tr key={symptom.idDS}>
                                                <td>{symptom.symptomName}</td>
                                                <button style={{ marginLeft: "10px" }} onClick={() => deleteSymptom(symptom.idSymptom)} className="btn btn-danger">Borrar</button>
                                            </tr>
                                    )
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
                <button className="btn btn-primary" onClick={() => addSymptomToDisease()}> Añadir síntomas</button>
                </div>
        </div>
    )
}

export default ViewDisease;
