import React from 'react';
import { useHistory } from 'react-router-dom';
import UserService from '../services/UserService';

const CreateUser = () => {
    const history = useHistory();


    const [id, setId] = useState('');
    const [password, setPassword] = useState('');

    const queryUser = () => {
        let user = { id: id, password: password };
        console.log('user => ' + JSON.stringify(user));

        //Step 5
        // UserService.queryUser(user.id, user.password).then(
        //     (res) => {
        //         console.log((res));
        //         if (!res) {
        //             alert.toString("Error");
        //         }
        //         else {
        //             localStorage.setItem('token', res);
        //             this.props.history.push('/users');
        //         }
        //     }).then();
        UserService.queryUser(user.id, user.password).then((res) => {
                let idextra = res.data;
                console.log(idextra);
                localStorage.setItem('token', res.data);
                history.push('/users');

            }, error => {
                console.log(error);
                console.log("Estamos en el error");
            });
    }

    const changeIdHandler = (event) => {
        setId(event.target.value);
    }

    const changePasswordHandler = (event) => {
        setPassword(event.target.value);
    }

    const cancel = () => {
        history.push('/users');
    }

    useEffect(() => {
        window.onpopstate = () => {
            history.push('/users');
        }
    });


    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h2>Inicio de sesión</h2>
                        <div className="card-body">
                            <form>
                                <div className="form-group">
                                    <label>Id: </label>
                                    <input placeholder="Id" name="id" className="form-control" value={id} onChange={(event) => changeIdHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Contraseña: </label>
                                    <input placeholder="Contraseña" name="password" className="form-control" value={password} onChange={(event) => changePasswordHandler(event)} />
                                </div>
                                <button className="btn btn-success" onClick={() => queryUser()}>Iniciar</button>
                                <button className="btn btn-danger" onClick={() => cancel()} style={{ marginLeft: "10px" }}>Cancelar</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default UserLogin;
