import React, { useEffect, useState } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import NotificationService from '../services/NotificationService'
import SearchPatient from './SearchPatient';
import BasicSnackBar from './BasicSnackBar';
import MultipleSelect from './BasicDropDown';

const ListNotification = () => {
    const history = useHistory();
    const idDoctor = localStorage.getItem('token');
    const location = useLocation();

    // Aqui se empiezan a definir las variables de estado
    const [notifications, setNotifications] = useState([]);
    const [open, setOpen] = useState(false);


    // Aqui se empiezan a definir las funciones del componente
    const deleteNotification = (idNotification) => {
        NotificationService.deleteNotification(idNotification).then(() => {
            const filteredNotifications = notifications.filter(notification => notification.id !== idNotification);
            setNotifications(filteredNotifications);
        });
        history.push(`/list-notifications/${idDoctor}`);
    }

    const viewNotification = (idNotification) => {
        history.push(`/view-notification/${idDoctor}/${idNotification}`);
    }

    const editAppointment = (idAppointment) => {
        history.push(`/update-notification/${idAppointment}`);
    }

    const addNotification = () => {
        history.push('/add-notification/add');
    }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            localStorage.removeItem('token');
            history.push('/');
        }
        console.log(localStorage.getItem('token'));
        NotificationService.getNotificationsOfDoctor(idDoctor).then((res) => {
            setNotifications(res.data);
        });
        console.log(location.search);
        if(location.search === '?true'){
            handleClick();
        }
    }, [location]);

    return (
        <div>
            <h2 className="text-center">Lista de Notificaciones</h2>
            <div className="row">
                <button className="btn btn-primary" style={{ marginRight: "703px" }} onClick={() => addNotification()}> Añadir notificación</button>
                {/* <button className="btn btn-primary" onClick={() => listSymptom()}> Lista Sintomas</button>
                <button className="btn btn-primary" onClick={() => listDisease()}> Lista Enfermedades</button>
                <button className="btn btn-primary" onClick={() => listMedicine()}> Lista Medicamentos</button> */}
                <MultipleSelect/>
            </div>
            {/* <div className="row">
                <SearchPatient patients={patients}/>
            </div> */}
            <div className="row">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th>Paciente</th>
                            <th>Fecha</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            notifications.map(
                                notification =>
                                    <tr key={notification.idNotification}>
                                        <td>{notification.idPatient}</td>
                                        <td>{notification.dateNotification}</td>
                                        <td>
                                            {/* <button style={{ marginLeft: "10px" }} onClick={() => editAppointment(appointment.idAppointment)} className="btn btn-info">Update</button> */}
                                            <button style={{ marginLeft: "10px" }} onClick={() => deleteNotification(notification.idNotification)} className="btn btn-danger">Borrar</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => viewNotification(notification.idNotification)} className="btn btn-info">Ver notificación</button>
                                        </td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>
                <BasicSnackBar 
                    open={open}
                    onClose={handleClose}
                    severity="success"
                    message="Notificación añadida correctamente"
                />
            </div>
        </div>
    )
}

export default ListNotification;
