import axios from 'axios';

const PATIENT_API_BASE_URL = "http://localhost:8080/api/v1/records";

class RecordService {

    createRecord(patient) {
        return axios.post(PATIENT_API_BASE_URL, patient);
    }

    getRecords() {
        return axios.get(PATIENT_API_BASE_URL);
    }

    getRecordsOfPatient(idPatient) {
       return axios.get(PATIENT_API_BASE_URL + '/' + idPatient);
    }

    getRecordById(idDoctor,idPatient) {
        return axios.get(PATIENT_API_BASE_URL + '/' + idDoctor + '/' + idPatient);
    }

    updateRecord(idPatient, patient) {
        return axios.put(PATIENT_API_BASE_URL + '/' + idPatient, patient);
    }

    deleteRecord(idPatient) {
        return axios.delete(PATIENT_API_BASE_URL + '/' + idPatient);
    }

}

export default new RecordService()
