import React, { useEffect, useState } from 'react';
import { useParams, useHistory, useLocation } from 'react-router-dom';
import PatientService from '../services/PatientService'
import DiseaseService from '../services/DiseaseService'
import BasicSnackBar from './BasicSnackBar'

const ViewPatient = () => {
    const params = useParams();
    const history = useHistory();
    const idDoctor = localStorage.getItem('token');
    const location = useLocation();

    // Aqui se empiezan a definir las variables de estado
    const [idP] = useState(params.idPatient);
    const [patient, setPatient] = useState({})
    const [diseases, setDiseases] = useState([])
    const [open, setOpen] = useState(false);

    const addDiseaseToPatient = () => {
        history.push(`/add-disease-to-patient/${idP}`);
    }

    const deleteDisease = (idDP) => {
        PatientService.deleteDiseaseOfPatient(idP, idDP).then(() => {
            const filteredDiseases = diseases.filter(disease => disease.id !== idDP);
            setDiseases(filteredDiseases);
        });
        window.location.reload();
    }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push(`/list-patients/${idDoctor}`);
        }
        PatientService.getPatientById(idDoctor, idP).then((res) => {
            setPatient(res.data);
        })
        //Busco los sintomas de la enfermedad con idD
        DiseaseService.getDiseasesOfPatient(idP).then((res) => {
            setDiseases(res.data);
        });
    }, []);

    const listRecords = () => {
        history.push(`/list-records/${idDoctor}/${idP}`);
    }

    return (
        <div>
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Detalles del paciente</h3>
                <div className="card-body">
                    <div className="row">
                        <label> Nombre: </label>
                        <div> {patient.firstName}</div>
                    </div>
                    <div className="row">
                        <label> Apellidos: </label>
                        <div> {patient.lastName}</div>
                    </div>
                    <div className="row">
                        <label> Email: </label>
                        <div> {patient.email}</div>
                    </div>
                    <div className="row">
                        <label> Cumpleaños: </label>
                        <div> {patient.birthday}</div>
                    </div>
                    <div className="row">
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th>Enfermedades</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    diseases.map(
                                        disease =>
                                            <tr key={disease.idDP}>
                                                <td>{disease.diseaseName}</td>
                                                <button style={{ marginLeft: "10px" }} onClick={() => deleteDisease(disease.idDisease)} className="btn btn-danger">Borrar</button>
                                            </tr>
                                    )
                                }
                            </tbody>
                        </table>
                    </div>
                    <div className="row">
                        <button className="btn btn-primary" onClick={() => addDiseaseToPatient()}> Añadir enfermedad</button>
                    </div>
                    {/* <button className="btn btn-primary" onClick={() => addDiseaseToPatient()}> Añadir enfermedad</button> */}
                </div>
            </div>
            <div className="row">
                <button className="btn btn-primary" onClick={() => listRecords()}> Ver registros</button>
            </div>
        </div>
    )
}

export default ViewPatient;
