import * as React from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogTitle from '@mui/material/DialogTitle';
import { useHistory } from 'react-router-dom';

const BasicAlertDialog = ({ idMedicine}) => {
    const history = useHistory();
    const [openDialog, setOpenDialog] = React.useState(false);

    const handleClickDelete = () => {
        setOpenDialog(true);
      };
    
      const handleConfirmDelete = () => {
        setOpenDialog(false);
        MedicineService.deleteMedicine(idMedicine);
        history.push('/list-medicines/');
      };
    
      const handleCancelDelete = () => {
        setOpenDialog(false);
      };

      return (
        <div>
          <Button variant="contained" color="secondary" onClick={handleClickDelete}>Eliminar</Button>
          <Dialog
            open={openDialog}
            onClose={handleCancelDelete}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogTitle id="alert-dialog-title">{"¿Estás seguro que quieres eliminar este elemento?"}</DialogTitle>
            <DialogActions>
              <Button onClick={handleCancelDelete} color="primary">Cancelar</Button>
              <Button onClick={handleConfirmDelete} color="primary" autoFocus>Aceptar</Button>
            </DialogActions>
          </Dialog>
        </div>
      );
    };

  export default BasicAlertDialog;
