import React, { useEffect, useState } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import UserService from '../services/UserService';
import BasicSnackBar from './BasicSnackBar';

const UserLogin = () => {
    const history = useHistory();
    const location = useLocation();

    // Aqui se empiezan a definir las variables de estado
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');
    const [examp, setExamp] = useState({});
    

    // Aqui se empiezan a definir las funciones del componente
    const queryUser = (event) => {
        event.preventDefault();
        let user = { id: id, password: password };
        console.log('user => ' + JSON.stringify(user));
        checkFormValues(user);

        //Comprobar que la infomacion de login es correcta
        // UserService.getUserById(id).then((res) => {
        //     setExamp(res.data);
        //     //console.log(res.data);
        // });
        console.log(examp.password);
        console.log(user.password);

        if(examp.password === user.password){
            UserService.queryUser(user.id, user.password).then((res) => {
                localStorage.setItem('token', res.data);
                if(user.id== 0){
                    history.push(`admin-view/${user.id}`);
                }
                else{
                    history.push(`/list-patients/${user.id}`);
                }
            }, error => {
                console.log(error);
                console.log("Estamos en el error");
            });
        }
        else{
            alert('Datos incorrectos');
            history.push('/');
        }
    }

    const checkFormValues = (user) => {
        if(user.id === ''){
            alert('El id no puede estar vacío');
            history.push('/');
        }
        else if (user.id < 0){
            alert('El id no puede ser negativo');
            history.push('/');
        }
        else if(isNaN(user.id)){
            alert('El id debe ser un número');
            history.push('/');
        }
        else if(user.password === ''){
            alert('La contraseña no puede estar vacía');
            history.push('/');
        }
    }

    const changeIdHandler = (event) => {
        setId(event.target.value);
    }

    const changePasswordHandler = (event) => {
        setPassword(event.target.value);
    }

    const cancel = () => {
        history.push('/');
    }

    const registrateUser = () => {
        history.push('/add-user/add');
    }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push('/');
        }
        //Comprobar que la infomacion de login es correcta
        UserService.getUserById(id).then((res) => {
            setExamp(res.data);
        });
        console.log(location.search);
        if(location.search === '?true'){
            handleClick();
        }
    }, [location]);


    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h2>Inicio de sesión</h2>
                        <div className="card-body">
                            <form>
                                <div className="form-group">
                                    <label>Id: </label>
                                    <input placeholder="Id" name="id" className="form-control" value={id} onChange={(event) => changeIdHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Contraseña: </label>
                                    <input placeholder="Contraseña" name="password" className="form-control" value={password} onChange={(event) => changePasswordHandler(event)} />
                                </div>
                                <button className="btn btn-success" onClick={(event) => queryUser(event)}>Iniciar</button>
                                <button type="button" className="btn btn-danger" onClick={() => cancel()} style={{ marginLeft: "10px" }}>Cancelar</button>
                            </form>
                        </div>
                        <button className="btn btn-primary" onClick={() => registrateUser()} style={{ marginLeft: "10px" }}>Registrate</button>
                    </div>
                </div>
                <BasicSnackBar 
                    open={open}
                    onClose={handleClose}
                    severity="success"
                    message="Enfermedad añadida correctamente"
                />
            </div>
        </div>
    )

}

export default UserLogin;
