import axios from 'axios';

const RECORD_API_BASE_URL = "http://localhost:8080/api/v1/records";

class RecordService {

    createRecord(record) {
        return axios.post(RECORD_API_BASE_URL, record);
    }

    getRecords() {
        return axios.get(RECORD_API_BASE_URL);
    }

    getRecordsOfPatient(idPatient) {
       return axios.get(RECORD_API_BASE_URL + '/' + idPatient);
    }

    getRecordById(idDoctor,idPatient) {
        return axios.get(RECORD_API_BASE_URL + '/' + idDoctor + '/' + idPatient);
    }

    updateRecord(idPatient, record) {
        return axios.put(RECORD_API_BASE_URL + '/' + idPatient, record);
    }

    deleteRecord(idPatient) {
        return axios.delete(RECORD_API_BASE_URL + '/' + idPatient);
    }

}

export default new RecordService()
