import * as React from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogTitle from '@mui/material/DialogTitle';

const BasicAlertDialog = ({ open, onClose, onClickDelete}) => {
    const [openDialog, setOpenDialog] = useState(false);

    const handleClickDelete = () => {
        setOpenDialog(true);
      };
    
      const handleConfirmDelete = () => {
        // Lógica para eliminar elemento
        setOpenDialog(false);
      };
    
      const handleCancelDelete = () => {
        setOpenDialog(false);
      };

      return (
        <div>
          <Button variant="contained" color="secondary" onClick={handleClickDelete}>Eliminar</Button>
          <Dialog
            open={openDialog}
            onClose={handleCancelDelete}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogTitle id="alert-dialog-title">{"Eliminar elemento"}</DialogTitle>
            <DialogContent>
              <DialogContentText id="alert-dialog-description">
                ¿Estás seguro de que deseas eliminar este elemento?
              </DialogContentText>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleCancelDelete} color="primary">
                Cancelar
              </Button>
              <Button onClick={handleConfirmDelete} color="primary" autoFocus>
                Aceptar
              </Button>
            </DialogActions>
          </Dialog>
        </div>
      );
    };

  export default BasicAlertDialog;
