import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import RecordService from '../services/RecordService'
import MedicineService from '../services/MedicineService'

const ViewRecord = () => {
    const params = useParams();
    const history = useHistory();

    // Aqui se empiezan a definir las variables de estado
    const [idP] = useState(params.idPatient);
    const [idR] = useState(params.idRecord);
    const [record, setRecord] = useState({})
    const [symptoms, setSymptoms] = useState([])
    const [medicines, setMedicines] = useState([])

    const addSymptomToRecord = () => {
        history.push(`/add-symptom-to-record/${idP}/${idR}`); //Modificar
    }

    // Aqui empieza los useEffect
    useEffect(() => {
        const idDoctor = localStorage.getItem('token');
        window.onpopstate = () => {
            history.push(`/list-records/${idDoctor}/${idP}`);
        }
        RecordService.getRecordById(idP, idR).then((res) => {
            setRecord(res.data);
        })
        //Busco los sintomas del registro con idD
        RecordService.getSymptomsOfRecord(idR).then((res) => {
            setSymptoms(res.data);
            console.log(symptoms);
            console.log(res.data);
            //Aquí hago la recomendación con los síntomas que he obtenido arriba
            MedicineService.getRecommendationForSymptoms(res.data).then((res) => {
                setMedicines(res.data);
            });
        });
    }, []);

    return (
        <div>
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Detalles del registro</h3>
                <div className="card-body">
                    <div className="row">
                        <label> Fecha: </label>
                        <div> {record.dateRecord}</div>
                    </div>
                    <div className="row">
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th>Sintomas</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    symptoms.map(
                                        symptom =>
                                            <tr key={symptom.idRS}>
                                                <td>{symptom.symptomName}</td>
                                            </tr>
                                    )
                                }
                            </tbody>
                        </table>
                    </div>
                    <button className="btn btn-primary" onClick={() => addSymptomToRecord()}> Añadir síntomas</button>
                    <div className="row">
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th>Recomendaciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    medicines.map( //Cambiar
                                        medicine =>
                                            <tr key={medicine.idMedicine}>
                                                <td>{medicine.medicineName}</td>
                                            </tr>
                                    )
                                }
                            </tbody>
                        </table>
                    </div>

                </div>
            </div>
        </div>
    )
}

export default ViewRecord;
