import React, { useEffect, useState } from 'react';
import { useHistory, useParams, useLocation } from 'react-router-dom';
import MedicineService from '../services/MedicineService'
import DiseaseService from '../services/DiseaseService'
import BasicSnackBar from './BasicSnackBar'

const ViewMedicine = () => {
    const history = useHistory();
    const params = useParams();
    const location = useLocation();

    // Aqui se empiezan a definir las variables de estado
    const [idM] = useState(params.id);
    const [medicine, setMedicine] = useState({})
    const [symptoms, setSymptoms] = useState([])
    const [diseases, setDiseases] = useState([])
    const [open, setOpen] = useState(false);

    const addSymptomToMedicine = () => {
        history.push(`/add-symptom-to-medicine/${idM}`);
    }

    const addDiseaseToMedicine = () => {
        history.push(`/add-disease-to-medicine/${idM}`);
    }

    const deleteSymptom = (idDS) => {
        MedicineService.deleteSymptomOfMedicine(idM, idDS).then(() => {
            const filteredSymptoms = symptoms.filter(symptom => symptom.id !== idDS);
            setSymptoms(filteredSymptoms);
        });
        window.location.reload();
    }

    const deleteDisease = (idDM) => {
        MedicineService.deleteDiseaseOfMedicine(idM, idDM).then(() => {
            const filteredDiseases = diseases.filter(disease => disease.id !== idDM);
            setDiseases(filteredDiseases);
        });
        window.location.reload();
    }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push('/list-medicines/');
        }
        MedicineService.getMedicineById(idM).then((res) => {
            setMedicine(res.data);
        });
        //Busco los sintomas del medicamento con idM
        MedicineService.getSymptomsOfMedicine(idM).then((res) => {
            setSymptoms(res.data);
        });
        //Busco las enfermedades del medicamento con idM
        MedicineService.getDiseasesOfMedicine(idM).then((res) => {  
            setDiseases(res.data);
        });
        console.log(location.search);
        if(location.search === '?true'){
            handleClick();
        }
    }, [location]);

    return (
        <div>
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Detalles del medicamento</h3>
                <div className="card-body">
                    <div className="row">
                        <label> Nombre: </label>
                        <div> {medicine.medicineName}</div>
                    </div>
                    <div className="row">
                        <label> Descripción: </label>
                        <div> {medicine.medicineDescription}</div>
                    </div>
                    <div className="row">
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th>Sintomas</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    symptoms.map(
                                        symptom =>
                                            <tr key={symptom.idDS}>
                                                <td>{symptom.symptomName}</td>
                                                <button style={{ marginLeft: "10px" }} onClick={() => deleteSymptom(symptom.idSymptom)} className="btn btn-danger">Borrar</button>
                                            </tr>
                                    )
                                }
                            </tbody>
                        </table>
                    </div>
                    </div>
                    <button className="btn btn-primary" onClick={() => addSymptomToMedicine()}> Añadir síntomas</button>
                    </div>
                    <div className="row">
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <th>Enfermedades con las que no se puede recomendar</th>
                                </tr>
                                </thead>
                                <tbody>
                                    {
                                    diseases.map(
                                        disease =>
                                            <tr key={disease.idDP}>
                                                <td>{disease.diseaseName}</td>
                                                <button style={{ marginLeft: "10px" }} onClick={() => deleteDisease(disease.idDisease)} className="btn btn-danger">Borrar</button>
                                            </tr>
                                    )
                                }
                            </tbody>
                        </table>
                    </div>
                    <button className="btn btn-primary" onClick={() => addDiseaseToMedicine()}> Añadir enfermedad</button>
            <BasicSnackBar 
                open={open}
                onClose={handleClose}
                severity="success"
                message="Información añadida correctamente al medicamento"
            />
        </div>
    )
}

export default ViewMedicine;
