import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import { useHistory, useParams } from 'react-router-dom';
import Autocomplete from '@mui/material/Autocomplete';

export default function SearchRecord({ records }) {
    const history = useHistory();
    const params = useParams();
    console.log(records);   
    const dateList = records.map((element, indice) => {return {index: indice, dateValue: element}});
    const idDoctor = localStorage.getItem('token');
    const [idP] = useState(params.idPatient);
    return (
        <Autocomplete
            disablePortal
            id="combo-box-demo"
            options={dateList} //Aquí va la lista de pacientes
            sx={{ width: 300 }}
            onChange={(_, indice) => history.push(`/view-record/${idDoctor}/${idP}/${indice}`)}
            renderOption={(props, option) => {
                return(
                    <li {...props} key={option.index}>
                        {option.dateValue}
                    </li>
                );
            }}
            //onChange={(event, value) => console.log(event)}
            renderInput={(params) => <TextField {...params} label="Busca registro" />}
        />
    );
}
