import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import UserService from '../../services/UserService';
import { sha256 } from 'crypto-js';

const CreateUser = () => {
    const params = useParams();
    const history = useHistory();

    // Aqui se empiezan a definir las variables de estado
    const [id] = useState(params.id);
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userPicture, setUserPicture] = useState('');
    const [emailList, setEmailList] = useState([]);
    let isWrong = false;

    // Aqui se empiezan a definir las funciones del componente
    const saveOrUpdateUser = (event) => {
        event.preventDefault();
        const cifrada = cifrarContraseña(password);
        console.log(cifrada);
        let user = { firstName: firstName, lastName: lastName, email: email, password: cifrada, userPicture: userPicture };
        checkFormValues(user);
        if (id === 'add' && !isWrong) {
            //Comprobar que no es un DNI repetido
            UserService.createUser(user).then(() => {
                history.push({
                    pathname: '/',
                    search: 'true'
                });
            });
        }
    }

    const checkFormValues = (user) => {
        console.log(emailList);
        console.log(user.email);
        if(emailList.includes(user.email)){
            alert('Este usuario ya existe');
            isWrong = true;
        }
        else if (user.firstName === '') {
            alert('El nombre no puede estar vacío');
            isWrong = true;
        }
        else if (!isNaN(user.firstName)) {
            alert('El nombre no puede ser un número');
            isWrong = true;
        }
        else if (user.lastName === '') {
            alert('El apellido no puede estar vacío');
            isWrong = true;
        }
        else if (!isNaN(user.lastName)) {
            alert('El apellido no puede ser un número');
            isWrong = true;
        }
        else if (user.email === '') {
            alert('El email no puede estar vacío');
            isWrong = true;
        }
        else if (user.password === '') {
            alert('La contraseña no puede estar vacía');
            isWrong = true;
        }
        else if (user.userPicture === '') {
            alert('El usuario debe tener una foto');
            isWrong = true;
        }
    }

    const changeFirstNameHandler = (event) => {
        setFirstName(event.target.value);
    }

    const changeLastNameHandler = (event) => {
        setLastName(event.target.value);
    }

    const changeEmailHandler = (event) => {
        setEmail(event.target.value);
    }

    const changePasswordHandler = (event) => {
        setPassword(event.target.value);
    }

    const changeUserPictureHandler = (event) => {
        setUserPicture(event.target.value);
    }

    const cancel = () => {
        history.push('/');
    }

    const getTitle = () => {
        return <h3 className="text-center">Añadir Usuario</h3>
    }

    const cifrarContraseña = (contraseña) => {
        const cifrado = sha256(contraseña).toString();
        return cifrado;
    }


    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push('/');
        }
        console.log('Hola');
        UserService.getUsersEmail().then((res) => {
            setEmailList(res.data);
        });
    });

    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        {
                            getTitle()
                        }
                        <div className="card-body">
                            <form onSubmit={(event) => saveOrUpdateUser(event)}>
                                <div className="form-group">
                                    <label>Nombre: </label>
                                    <input placeholder="Nombre" name="firstName" className="form-control" value={firstName} onChange={(event) => changeFirstNameHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Apellidos: </label>
                                    <input placeholder="Apellidos" name="lastName" className="form-control" value={lastName} onChange={(event) => changeLastNameHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Email: </label>
                                    <input placeholder="Email" name="email" className="form-control" value={email} onChange={(event) => changeEmailHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Contraseña: </label>
                                    <input placeholder="Contraseña" name="password" className="form-control" value={password} onChange={(event) => changePasswordHandler(event)} />
                                </div>
                                <div className="form-group">
                                    <label> Foto(Link): </label>
                                    <input placeholder="Foto" name="userPicture" className="form-control" value={userPicture} onChange={(event) => changeUserPictureHandler(event)} />
                                </div>
                                <button type="submit" className="btn btn-success">Guardar</button>
                                <button type="button" className="btn btn-danger" onClick={() => cancel()} style={{ marginLeft: "10px" }}>Cancelar</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default CreateUser;
