import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import MedicineService from '../services/MedicineService'

const ListMedicine = () => {
    const history = useHistory();
    const idDoctor = localStorage.getItem('token');


    // Aqui se empiezan a definir las variables de estado
    const [medicines, setMedicines] = useState([]);


    // Aqui se empiezan a definir las funciones del componente
    const deleteMedicine = (idMedicine) => {
        MedicineService.deleteMedicine(idMedicine).then(() => {
            const filteredMedicines = medicines.filter(medicine => medicine.id !== idMedicine);
            setMedicines(filteredMedicines);
        });
        MedicineService.deleteSymptomsOfMedicine(idMedicine);
        history.push('/list-medicines/');
    }

    const viewMedicine = (idMedicine) => {
        history.push(`/view-medicine/${idMedicine}`);
    }

    const editMedicine = (idMedicine) => {
        history.push(`/update-medicine/${idMedicine}`);
    }

    const addMedicine = () => {
        history.push('/add-medicine/add');
    }

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push(`/list-patients/${idDoctor}`);
        }
        console.log(localStorage.getItem('token'));
        MedicineService.getMedicines().then((res) => {
            setMedicines(res.data);
        });
    }, []);

    return (
        <div>
            <h2 className="text-center">Lista de Medicamentos</h2>
            <div className="row">
                <button className="btn btn-primary" onClick={() => addMedicine()}> AÃ±adir Medicamento</button>
            </div>
            <div className="row">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th>Nombre</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            medicines.map(
                                medicine =>
                                    <tr key={medicine.idMedicine}>
                                        <td>{medicine.medicineName}</td>
                                        <td>
                                            <button style={{ marginLeft: "10px" }} onClick={() => editMedicine(medicine.idMedicine)} className="btn btn-info">Update</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => deleteMedicine(medicine.idMedicine)} className="btn btn-danger">Borrar</button>
                                            <button style={{ marginLeft: "10px" }} onClick={() => viewMedicine(medicine.idMedicine)} className="btn btn-info">Detalles</button>
                                        </td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )

}

export default ListMedicine;
