import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import UserService from '../../services/UserService'

const ViewUser = () => {
    const params = useParams();
    const history = useHistory();

    // Aqui se empiezan a definir las variables de estado
    const [id, setId] = useState(params.id);
    const [user, setUser] = useState({})
    const { firstName, lastName, email, city, workplace, specialty, userPicture } = user;

    const handleUpdateUser = () => updateUser()

    const updateUser = () => {
        history.push(`/update-user/${id}`);
    }

    const handleUpdatePassword = () => updatePassword()

    const updatePassword = () => {
        history.push(`/update-password/${id}`);
    }

    const handleDeleteAlert = () => handleDelete()

    const handleDelete = () => {
        const confirmed = window.confirm('¿Estás seguro de que borrar esta cuenta del sistema?');
        if (confirmed) {
            deleteUser();
        }
    };

    const deleteUser = () => {
        UserService.deleteUser(id).then(() => {
        });
        localStorage.removeItem('token'); //Cuando se borre cerramos sesión
        setId('')
        history.push('/');
    }

    const renderPicture = () => {
        if (user.userPicture === "") {
            return <div>Sin foto</div>
        }
        else {
            return <img class="view-user-picture" src={userPicture} alt="new" />
        }
    }

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push(`/list-patients/${id}`);
        }
        UserService.getUserById(id).then((res) => {
            setUser(res.data);
        })
    }, []);

    return (
        <div>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Detalles del usuario</h3>
                <div className="card-body">
                    <div className="row">
                        <label> Nombre: </label>
                        <div> {firstName}</div>
                    </div>
                    <div className="row">
                        <label> Apellidos: </label>
                        <div> {lastName}</div>
                    </div>
                    <div className="row">
                        <label> Email: </label>
                        <div> {email}</div>
                    </div>
                    <div className="row">
                        <label> Ciudad: </label>
                        <div> {city}</div>
                    </div>
                    <div className="row">
                        <label> Lugar de trabajo: </label>
                        <div> {workplace}</div>
                    </div>
                    <div className="row">
                        <label> Especialidad: </label>
                        <div> {specialty}</div>
                    </div>
                    <div className="row">
                        <label> Foto: </label>
                        {renderPicture()} 
                    </div>
                </div>
                <div className="row">
                    <button className="btn btn-primary" onClick={handleUpdateUser}>Modificar datos</button>
                    <button className="btn btn-primary" onClick={handleUpdatePassword}>Cambiar contraseña</button>
                    <button className="btn btn-danger" onClick={handleDeleteAlert}>Eliminar usuario</button>
                </div>
            </div>
        </div>
    )
}

export default React.memo(ViewUser);
