import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import { useHistory, useParams } from 'react-router-dom';
import Autocomplete from '@mui/material/Autocomplete';
import Box from '@mui/material/Box';

export default function SearchRecord({ records }) {
    const history = useHistory();
    const params = useParams();
    //console.log(records);   
    
    //const dateList = records.map((element, indice) => {return {idRecord: indice, dateRecord: element}});
    
    console.log(records);
    const idDoctor = localStorage.getItem('token');
    const [idP] = useState(params.idPatient);
    return (
        <Autocomplete
            disablePortal
            id="combo-box-demo"
            options={records} //Aquí va la lista de pacientes
            sx={{ width: 300 }}
            key={option => option.idRecord} //De Miguel
            getOptionLabel={(option) => option.dateRecord}
            onChange={(_, indice) => history.push(`/view-record/${idDoctor}/${idP}/${indice}`)}
            renderOption={(props, option) => ( //De Miguel
                <Box 
                component="li" 
                key={option.idRecord}
                {...props}
                >
                    {option.dateValue}
                </Box>
            )}
            //onChange={(event, value) => console.log(event)}
            renderInput={(params) => <TextField {...params} label="Busca registro" />}
        />
    );
}
