import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import {Dropdown, DropdownItem, DropdownMenu, DropdownToggle} from 'reactstrap';
import DiseaseService from '../services/DiseaseService'

const ViewDisease = () => {
    const history = useHistory();
    const params = useParams();

    // Aqui se empiezan a definir las variables de estado
    const [idD] = useState(params.id);
    const [disease, setDisease] = useState({})
    const [dropdown, setDropdown] =  useState(false);

    const openCloseDropdown = () => {
        setDropdown(!dropdown);
    }

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push('/list-diseases/');
        }
        DiseaseService.getDiseaseById(idD).then((res) => {
            setDisease(res.data);
        })
    }, []);

    return (
        <div>
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Detalles de la enfermedad</h3>
                <div className="card-body">
                    <div className="row">
                        <label> Nombre: </label>
                        <div> {disease.diseaseName}</div>
                    </div>
                    <div className="row">
                        <label> Descripción: </label>
                        <div> {disease.diseaseDescription}</div>
                    </div>
                </div>
                <Dropdown isOpen={dropdown} toggle={openCloseDropdown} size="lg">
                    <DropdownToggle caret>
                        Dropdown ejemplo
                    </DropdownToggle>
        
                    <DropdownMenu>
                        <DropdownItem header>Síntomas</DropdownItem>
                        <DropdownItem>Acción 1</DropdownItem>
                        <DropdownItem>Acción 2</DropdownItem>
                        <DropdownItem>Acción 3</DropdownItem>
                        <DropdownItem>Acción 4</DropdownItem>
                        <DropdownItem>Acción 5</DropdownItem>
                    </DropdownMenu>
                </Dropdown>
            </div>
        </div>
    )
}

export default ViewDisease;
