import React, { useEffect, useState } from 'react';
import { useParams, useHistory, useLocation } from 'react-router-dom';
import AppointmentService from '../../services/AppointmentService';
import PatientService from '../../services/PatientService';
import DiseaseService from '../../services/DiseaseService'
import BasicSnackBar from '../common/BasicSnackBar'

const ViewAppointment = () => {
    const params = useParams();
    const history = useHistory();
    const idDoctor = localStorage.getItem('token');
    const location = useLocation();
    

    // Aqui se empiezan a definir las variables de estado
    const [idA] = useState(params.idAppointment);
    const [appointment, setAppointment] = useState({})
    //const [diseases, setDiseases] = useState([])
    const [open, setOpen] = useState(false);
    const [patient, setPatient] = useState({})
    const { idPatient, dateAppointment, appointmentDescription } = appointment;

    // const addDiseaseToPatient = () => {
    //     history.push(`/add-disease-to-patient/${idP}`);
    // }

    // const deleteDisease = (idDP) => {
    //     PatientService.deleteDiseaseOfPatient(idP, idDP).then(() => {
    //         const filteredDiseases = diseases.filter(disease => disease.id !== idDP);
    //         setDiseases(filteredDiseases);
    //     });
    //     history.push(`/view-patient/${idDoctor}/${idP}`);
    // }

    const handleClick = () => {
        setOpen(true);
    };

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };

    // Aqui empieza los useEffect
    useEffect(() => {
        window.onpopstate = () => {
            history.push(`/list-appointments/${idDoctor}/id(increasing)`);
        }
        AppointmentService.getAppointmentById(idA).then((res) => {
            setAppointment(res.data);
            PatientService.getPatientById(idDoctor, res.data.idPatient).then((res) => {
                setPatient(res.data);
            })
        })
        //Busco los sintomas de la enfermedad con idD
        // DiseaseService.getDiseasesOfPatient(idP).then((res) => {
        //     setDiseases(res.data);
        // });
    },[]);

    // const listRecords = () => {
    //     history.push(`/list-records/${idDoctor}/${idP}`);
    // }

    return (
        <div>
            <br></br>
            <div className="card col-md-6 offset-md-3">
                <h3 className="text-center"> Detalles de la cita</h3>
                <div className="card-body">
                    <div className="row">
                        <label> Paciente: </label>
                        <div> {appointment.idPatient}</div>
                    </div>
                    <div className="row">
                        <label> Nombre: </label>
                        <div> {patient.firstName}</div>
                    </div>
                    <div className="row">
                        <label> Apellidos: </label>
                        <div> {patient.lastName}</div>
                    </div>
                    <div className="row">
                        <label> Fecha: </label>
                        <div> {appointment.dateAppointment}</div>
                    </div>
                    <div className="row">
                        <label> Descripción: </label>
                        <div> {appointment.appointmentDescription}</div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default React.memo(ViewAppointment);
